<?php
/* Alternate Styles & Layouts Functions */
global $arras_registered_alt_layouts, $arras_registered_style_dirs;

function register_alternate_layout($id, $name) {
	global $arras_registered_alt_layouts;
	$arras_registered_alt_layouts[$id] = $name;
}

function register_style_dir($dir) {
	global $arras_registered_style_dirs;
	$arras_registered_style_dirs[] = $dir;
}

function is_valid_arras_style($file) {
	return (bool)( !preg_match('/^\.+$/', $file) && preg_match('/^[A-Za-z][A-Za-z0-9\-]*.css$/', $file) );
}

function arras_override_styles() {
?>

<!-- Generated by Arras Theme -->
<style type="text/css">
<?php do_action('arras_custom_styles'); ?>
</style>
<?php
}

function arras_add_custom_logo() {
	$arras_logo_id = arras_get_option('logo');
	if ($arras_logo_id != 0) {
		$arras_logo = wp_get_attachment_image_src($arras_logo_id, 'full');

		echo '.blog-name a { background: url(' . $arras_logo[0] . ') no-repeat; text-indent: -9000px; width: ' . $arras_logo[1] . 'px; height: ' . $arras_logo[2] . 'px; display: block; }' . "\n";
	}
}

function arras_layout_styles() {

$node_based_size = arras_get_image_size('node-based-thumb');
$node_based_w = $node_based_size['w'];
$node_based_h = $node_based_size['h'];

$quick_preview_size = arras_get_image_size('quick-preview-thumb');
$quick_preview_w = $quick_preview_size['w'];
$quick_preview_h = $quick_preview_size['h'];
?>

.posts-default .post { width: <?php echo $node_based_w + 10 ?>px; }
.posts-default img, .posts-default .entry-thumbnails-link { width: <?php echo $node_based_w ?>px; height: <?php echo $node_based_h ?>px; }
.posts-default .entry-thumbnails { width: <?php echo $node_based_w + 10 ?>px; height: <?php echo $node_based_h + 10 ?>px; }
.posts-quick .entry-thumbnails img { width: <?php echo $quick_preview_w ?>px; height: <?php echo $quick_preview_h ?>px; }
.posts-default .entry-meta { width: <?php echo $node_based_w ?>px; }
.posts-quick .entry-meta { width: <?php echo $quick_preview_w ?>px; }

<?php
$slideshow_size = arras_get_image_size('featured-slideshow-thumb');
$slideshow_size_w = $slideshow_size['w'];
$slideshow_size_h = $slideshow_size['h'];
?>

.featured { height: <?php echo $slideshow_size_h + 10 ?>px; }
.featured-article { width: <?php echo $slideshow_size_w ?>px; height: <?php echo $slideshow_size_h ?>px; }
.featured-article img { width: <?php echo $slideshow_size_w ?>px; height: <?php echo $slideshow_size_h ?>px; }
#controls { width: <?php echo $slideshow_size_w - 30 ?>px; top: <?php echo ($slideshow_size_h / 2) - 15 ?>px; }
#controls .next { width: <?php echo $slideshow_size_w - 30 ?>px; }
.featured-entry { height: <?php echo ceil($slideshow_size_h / 3) ?>px; top: -<?php echo ceil($slideshow_size_h / 3) ?>px; }

<?php
$sidebar_size = arras_get_image_size('sidebar-thumb');
$sidebar_size_w = $sidebar_size['w'];
?>
.featured-stories-summary  { margin-left: <?php echo $sidebar_size_w + 15 ?>px; }
<?php
}

function arras_add_blueprint_css() {
	echo '
	<link rel="stylesheet" href="' . get_bloginfo('template_url') . '/css/blueprint/screen.css" type="text/css" media="screen,projection" />
	<link rel="stylesheet" href="' . get_bloginfo('template_url') . '/css/blueprint/print.css" type="text/css" media="print" />
	<!--[if IE 6]>
	<link rel="stylesheet" href="' . get_bloginfo('template_url') . '/css/blueprint/ie.css" type="text/css" media="screen,projection" />
	<![endif]-->
	';
}

function arras_add_layout_css() {
	global $arras_registered_alt_layouts;
	
	if ( count($arras_registered_alt_layouts) > 0 ) {
		
		if ( defined('ARRAS_FORCE_LAYOUT') ) {
			$layout = ARRAS_FORCE_LAYOUT;
		} else {
			$layout = arras_get_option('layout');
		}
	
		?><link rel="stylesheet" href="<?php bloginfo('template_url') ?>/css/layouts/<?php echo $layout ?>.css" type="text/css" /><?php
	}
}

function arras_add_style_css() {
	global $theme_data, $arras_registered_alt_styles;


	$scheme = arras_get_option('style');
	if ( $scheme != 'default' )
		echo '<link rel="stylesheet" href="' . get_bloginfo('template_url') . '/css/styles/' . $scheme . '.css" type="text/css" media="screen,projection" />';
	else
		echo '<link rel="stylesheet" href="' . get_bloginfo('template_url') . '/css/styles/default.css" type="text/css" media="screen,projection" />';
		
	if ( $scheme == 'default' || $scheme == 'legacy' ) { ?>
	<!--[if IE 6]>
	<link rel="stylesheet" href="<?php bloginfo('template_url'); ?>/css/ie6.css" type="text/css" />
	<![endif]-->
	<?php }
}

function arras_add_user_css() {
	if (!ARRAS_CHILD) {
		echo '<link rel="stylesheet" href="' . get_bloginfo('template_url') . '/user.css" type="text/css" media="screen,projection" />';
	} else {
		echo '<link rel="stylesheet" href="' . get_bloginfo('stylesheet_url') . '" type="text/css" media="screen,projection" />';
	}
}

